import React, {Component} from 'react'

  export default class Update extends Component {
    constructor (props) {
      super(props)
      this.state = {
        character: '',
        type: ''
      }
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }
      handleChange (event) {
   this.setState({ [event.currentTarget.id]: event.currentTarget.value})
 }
    async handleSubmit (event) {
    event.preventDefault()
    try{
      let response = await fetch('http://localhost:3003' + '/amiibos/' + event._id, {
          method: 'PUT', // Put, Delete. Only for a non-Get request
          body: JSON.stringify({character: this.state.character, type: this.state.type}),
          headers: {
              'Content-Type': 'application/json'
            }
          })
          let data =  await response.json()
          this.props.update(data)
          this.setState({
            character: '',
            type: ''
          })
        }catch(e){
          console.error({'Error': e})
        }
      }

    render(){
      return (
      <div>
        <form className="float-right" onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor="character">Character Name</label>
            <input type="text" id="character" name="character" onChange={this.handleChange} />
            <label htmlFor="type">Amiibo Type</label>
            <input type="text" id="type" name="type"
            onChange={this.handleChange}/>
            <input type="submit" value="Update Amiibo" />
          </div>
        </form>
      </div>

      )
    }
  }
